package com.perscholas;

public class mathexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

    	String message = " Welcome to Java";
		System.out.println("The length of " + message + " is " + message.length());
		System.out.println("the 4th character of " + message + "is " + message.charAt(4));
		System.out.println("the concate" + message + " is " + message.concat("at perscholas"));
		System.out.println("uppercase of " + message + " is " + message.toUpperCase());
		System.out.println("lowercase of " + message + " is " + message.toLowerCase());
		System.out.println("trim of " + message + " is " + message.trim());
		
	}

}
